<?php    if (isset($_GET['l'])) {        if ($_GET['l']=='ro')             setcookie('lang', 'ro');        else             setcookie('lang', 'en');    }    foreach (scandir('.') as $f) {         if (is_dir($f) && substr($f,0,1)=='2') {             $editions[] = $f;        }      }     if (isset($_GET['d'])) {        if (in_array($_GET['d'],$editions)) {            if (isset($_GET['p']))                header('Location: '.$_GET['d'].'?p='.$_GET['p']);            else                 header('Location: '.$_GET['d']);        }        else             header('Location: .');      }    else         header('Location: .');        