<div id="editions">    <div id="editions_links"><?php         $par = '../';                                       // path to parent directory        $cur = substr(dirname($_SERVER['PHP_SELF']),1);     // name of current directory        foreach (scandir($par) as $f) {                     // go through all files and folders in parent directory            if (is_dir($par.$f) && substr($f,0,1)=='2') {   // if it is a directory and starts with '2'                $editions[] = $f;            }                                               // end if        }                                                   // end cycle        foreach($editions as $e) {  // go through all editions                    echo "<a href=$par$e";  // create a link to it            if ($e==$cur) { echo " id=\"this_edition\""; }            if ($e==$editions[0]) { echo " class=\"first_edition\""; }            if ($e==end($editions)) { echo " class=\"last_edition\""; }            echo ">$e</a> ";        // close link to it        }    ?></div>    <div id="editions_title">Select edition &raquo;</div></div>